import React from 'react'

const Profile = () => {
    useEffect(() => {
        axios
          .get(`http://localhost:3000/comany/${id}`)
          .then((response) => {
            setTitle((response.data.title));
            setDescription(response.data.description);
          })
          .catch((error) => {
            console.error(error);
            // Afficher une erreur à l'utilisateur
          });
      }, [id]);
  return (
    <div>Profile</div>
  )
}

export default Profile
