import React, { useState } from 'react';
import axios from 'axios';
import { useEffect } from 'react';

const Company = () => {
    const [trainingCourses, setTrainingCourses] = useState([]);
    useEffect(() => {
        const fetchTrainingCourses = async () => {
          try {
            const res = await axios.get('http://localhost:3000/training-courses');
            setTrainingCourses(res.data);
          } catch (error) {
            console.error(error);
          }
        };
        fetchTrainingCourses();
      }, []);
      
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        phone: '',
        companyName: '',
        trainingCourse:null,
        message: ''
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          const res = await axios.post('http://localhost:3000/submit-company', formData);
          console.log(res.data);
          alert('Data successfully inserted into the database');
        } catch (error) {
          console.error(error);
          alert('Failed to insert data into the database');
        }
    };        
    const handleChange = (e) => {
      const { name, value } = e.target;
      
      if (name === 'trainingCourse') {
        const selectedCourse = trainingCourses.find(course => course.id_formation === parseInt(value));
        console.log(selectedCourse.title)
        setFormData({
          ...formData,
          [name]: parseInt(value),
          trainingcourse: selectedCourse.title
        });
      } else {
        setFormData({
          ...formData,
          [name]: value
        });
      }
      
    };
         

    return (
    <form className="max-w-lg mx-auto" onSubmit={handleSubmit}>
        <div class="flex justify-center items-center h-16 bg-gray-100 text-gray-900 font-bold text-lg uppercase tracking-wide">
        OUR PRODUCTS
      </div>
        <div className="mb-4 my-2">
        <label htmlFor="email" className="block text-gray-700 font-bold mb-2">
        Email
        </label>
        <input
            type="email"
            id="email"
            name="email"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            onChange={handleChange}
            value={formData.email}
            required
            />
        </div>
        <div className="mb-4">
        <label htmlFor="password" className="block text-gray-700 font-bold mb-2">
        Password
        </label>
        <input
            type="password"
            id="password"
            name="password"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            onChange={handleChange}
            value={formData.password}
            required
            />
        </div>
        <div className="mb-4">
        <label htmlFor="phone" className="block text-gray-700 font-bold mb-2">
        Phone
        </label>
        <input
            type="tel"
            id="phone"
            name="phone"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            onChange={handleChange}
            value={formData.phone}
            required
            />
        </div>
        <div className="mb-4">
        <label htmlFor="companyName" className="block text-gray-700 font-bold mb-2">
        Company Name
        </label>
        <input
            type="text"
            id="companyName"
            name="companyName"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            onChange={handleChange}
            value={formData.companyName}
            required
            />
        </div>
        <div className="mb-4">
            <label htmlFor="trainingCourse" className="block text-gray-700 font-bold mb-2">
                Training Course
            </label>
            <select
                name="trainingCourse"
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                onChange={handleChange}
                required
            >
                <option value="">-- Select a Training Course --</option>
                {trainingCourses.length > 0 && trainingCourses.map((course) => (
                    <option key={course.id_formation} value={course.id_formation}>
                        {course.title}
                    </option>
                ))}
            </select>
        </div>
        <div className="mb-4">
        <label htmlFor="message" className="block text-gray-700 font-bold mb-2">
        Message
        </label>
        <textarea
            id="message"
            name="message"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            rows="5"
            onChange={handleChange}
            value={formData.message}
            required
            ></textarea>
        </div>
        <div className="flex justify-center">
        <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mb-3"
            >
        Submit
        </button>
        </div>
    </form>
    );
};
export default Company
