import React, { useEffect, useState } from "react";
import axios from "axios";
import "tailwindcss/tailwind.css";

const CompanyDetail = () => {
  const [companies, setCompanies] = useState("");
  const [clicked , setClicked] = useState();

  useEffect(() => {
    axios
      .get(`http://localhost:3000/checkCourses/4t8a`)
      .then((response) => {
        setCompanies(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <table className="min-w-full divide-y divide-gray-200">
      <thead className="bg-gray-50">
        <tr>
          <th
            scope="col"
            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            Nom de formation
          </th>
          <th
            scope="col"
            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            accepter
          </th>
        </tr>
      </thead>
      <tbody className="bg-white divide-y divide-gray-200">
        <td className="px-6 py-4 whitespace-nowrap">
          <div className="text-sm text-gray-900">{companies}</div>
        </td>
        {clicked && <button className="bg-green-500 mt-4 text-white flex justify-center items-center focus:outline-none hover:bg-green-600 rounded-md text-lg shadow-md">
          Envoyer Ã  l'utilisateur
        </button>}
      </tbody>
    </table>
  );
};

export default CompanyDetail;
