import axios from "axios";
import React, { createContext, useEffect, useState } from "react";
import Navbar2 from "./Navbar2";
import OurService from "./OurService";
import WaitForAdminConfirmation from "./WaitForAdminConfirmation";
//pour partager les données entre tous les components
export const CompanyContext = createContext();
const Profile = () => {
  const [code, setCode] = useState("");
  const [id, setId] = useState(0);
  const [image, setImage] = useState(0);

  const email = localStorage.getItem("email");
  
  useEffect(() => {
    axios.get(`http://localhost:3000/company/${email}`)
      .then((response) => {
        const company = response.data[0];
        setCode(company.code);
        setId(company.company_id);
        setImage(company.image)
      })
      .catch((error) => {
        console.error(error);
      });
  }, [email]);

  return (
    <div>
    <CompanyContext.Provider value={id}>
      <Navbar2 code={code} image={image}  />
      {code ? <OurService code={code} /> : <WaitForAdminConfirmation/>}
    </CompanyContext.Provider>
    </div>
  );
};

export default Profile;
