import React, { useState, useEffect, Fragment } from "react";
import axios from "axios";
import "tailwindcss/tailwind.css";
import { useNavigate, useParams } from "react-router-dom";

const CompanyDetail = () => {
  const [courses, setCourses] = useState([]);
  const { id } = useParams();
  useEffect(() => {
    axios
      .get(`http://localhost:3000/company/${id}/courses`)
      .then((response) => setCourses(response.data))
      .catch((error) => console.error(error));
  }, [id]);

  const navigate = useNavigate();
  const goToDashboard = () => {
    navigate("/dashboard/account");
  };
  //pour activer boutton
  const [inProgress, setInProgress] = useState(false);
  const updateInProgress = async (courseId) => {
    try {
      const response = await axios.put(
        `http://localhost:3000/company/${id}/courses/${courseId}`,
        { inProgress: !inProgress }
      );
      setInProgress(!inProgress);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Fragment>
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            <th
              scope="col"
              className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
            >
              Nom de formation
            </th>
            <th
              scope="col"
              className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
            >
              Etat
            </th>
            <th></th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {courses.map((course) => (
            <tr key={course.course_id}>
              <td className="px-6 py-4 whitespace-nowrap">
                <div className="text-sm text-gray-900">{course.title}</div>
              </td>
              <td className="px-6 py-4 whitespace-nowrap">
                <button
                  className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
                  onClick={() => updateInProgress(course.course_id)}
                >
                  {course.inProgress ? "Désactiver" : "Activer"}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="flex justify-center mt-6">
        <button
          onClick={goToDashboard}
          className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Go back
        </button>
      </div>
    </Fragment>
  );
};

export default CompanyDetail;
