  function handleUsernameChange(event) {
    setUsername(event.target.value);
  }

  function handleEmailChange(event) {
    setEmail(event.target.value);
  }

  function handleSave() {
    // Code pour enregistrer les données du formulaire
  }

  function handleCancel() {
    // Code pour annuler l'enregistrement et vider le formulaire
  }
  const [image, setImage] = useState(null);
  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };
  return (
    <form className="flex flex-col space-y-6 border p-4 bg-white mr-5 mt-20">
    <div className="flex items-center space-x-4 mb-2 ml-10">
        <div className="relative">
        <input
        type="file"
        id="image"
        name="image"
        accept="image/*"
        onChange={handleImageChange}
        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
        </div>
      </div>
      <div className='mr-60 ml-10 '>
      
      <hr/>
      </div>
    </form>
    );
}

export default EditInformations;
