import React, { useState } from "react";

function Tableau() {
const [tableau, setTableau] = useState([]);

const handleAddRow = (e) => {
e.preventDefault();
const newRow = {
idFormation,
title,
description,
image,
apkFile,
};
setTableau([...tableau, newRow]);
};

return (
<div className="max-w-md mx-auto my-8">
<h2 className="text-2xl font-bold mb-4">Training Course Table</h2>
<table className="table-auto">
<thead>
<tr>
<th className="px-4 py-2">ID</th>
<th className="px-4 py-2">Title</th>
<th className="px-4 py-2">Description</th>
<th className="px-4 py-2">Image</th>
<th className="px-4 py-2">APK File</th>
</tr>
</thead>
<tbody>
{tableau.map((row, index) => (
<tr key={index}>
<td className="border px-4 py-2">{row.idFormation}</td>
<td className="border px-4 py-2">{row.title}</td>
<td className="border px-4 py-2">{row.description}</td>
<td className="border px-4 py-2">
<img src={row.image} alt={row.title} width="50" />
</td>
<td className="border px-4 py-2">{row.apkFile}</td>
</tr>
))}
</tbody>
</table>
</div>
);
}

export default Tableau;
