import { useState } from 'react';

function EditInformations() {
  const [image, setImage] = useState(null);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');

  function handleImageChange(event) {
    setImage(URL.createObjectURL(event.target.files[0]));
  }

  function handleUsernameChange(event) {
    setUsername(event.target.value);
  }

  function handleEmailChange(event) {
    setEmail(event.target.value);
  }

  function handleSave() {
    // Code pour enregistrer les données du formulaire
  }

  function handleCancel() {
    // Code pour annuler l'enregistrement et vider le formulaire
  }

  return (
    <form className="flex flex-col space-y-6 border p-4 bg-white mr-5 ">
      <div className="flex items-center space-x-4">
        <label htmlFor="image" className="font-medium text-gray-700">
          Image
        </label>
        <div className="relative">
          <input
            id="image"
            name="image"
            type="file"
            accept="image/*"
            className="sr-only"
            onChange={handleImageChange}
          />
          <div className="h-12 w-12 bg-gray-100 rounded-md">
            {image && (
              <img
                src={image}
                alt="Preview"
                className="object-cover w-full h-full rounded-md"
              />
            )}
          </div>
          <div className="absolute inset-0 cursor-pointer">
            <div className="h-full w-full bg-gray-100 opacity-0 hover:opacity-30 rounded-md"></div>
            <div className="absolute inset-0 flex items-center justify-center">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6 text-gray-500"
                viewBox="0 0 20 20"
                fill="currentColor"
              >
                <path
                  fillRule="evenodd"
                  d="M17 8v7a2 2 0 01-2 2H5a2 2 0 01-2-2V8h2v7h10V8h2zm-9-6a2 2 0 012 2v2a2 2 0 11-4 0V4a2 2 0 012-2zM9 7a1 1 0 011 1v5a1 1 0 11-2 0V8a1 1 0 011-1zM3 8v7a4 4 0 004 4h6a4 4 0 004-4V8a2 2 0 00-2-2H5a2 2 0 00-2 2z"
                  clipRule="evenodd"
                />
              </svg>
            </div>
          </div>
        </div>
      </div>
      <div className='m'>
      <div>
        <label htmlFor="username" className="font-medium text-gray-700">
          Username
        </label>
        <input
          id="username"
          name="username"
          type="text"
          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
          onChange={handleUsernameChange}
          value={username}
        />
      </div>
      <div>
        <label htmlFor="email" className="font-medium text-gray-700">
          Email
        </label>
        <input
          id="email"
          name="email"
          type="email"
          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
          onChange={handleEmailChange}
          value={email}
        />
      </div>
      </div>
      <div className="flex justify-end">
        <button
          type="button"
          className="px-4 py-2 mr-2 border border-transparent text-base font-medium rounded-md text-white bg-indigo-500 hover:bg-indigo-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          onClick={handleSave}
        >
          Enregistrer
        </button>
        <button
          type="button"
          className="px-4 py-2  border border-transparent text-base font-medium rounded-md text-gray-700 bg-gray-100 hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          onClick={handleCancel}
        >
          Annuler
        </button>
      </div>
    </form>
    );
}

export default EditInformations;
