/* eslint-disable react-hooks/rules-of-hooks */
import axios from "axios";
import React, { useEffect, useState } from "react";
import { Fragment } from "react";
import { useShoppingCart } from "../context/ShoppingCartContext";
import "./OurService.css";

function OurService(props) {
  const [tableauData, setTableauData] = useState([]);
  const { getItemQuantity, increaseCartQuantity, removeFromCart } =
    useShoppingCart();

  useEffect(() => {
    axios
      .get(`http://localhost:3000/company/${props.id}/courses`) // ajout de guillemets autour de l'URL
      .then((response) => {
        const courses = response.data.map((course) => ({
          ...course,
          isPurchase: true,
        })); // ajout de la propriété isPurchase à chaque cours acheté
        setTableauData(courses); // mise à jour de l'état avec les données récupérées
      })
      .catch((err) => console.error(err));
  }, [props.id]);

  return (
    <Fragment>
      <div className="flex justify-center items-center h-16 bg-gray-100 text-gray-900 font-bold text-lg uppercase tracking-wide">
        OUR SERVICES
      </div>
      <div className="flex flex-wrap justify-center">
        {tableauData.map((course, index) => (
          <div
            className="max-w-sm mx-4 my-4 rounded-lg overflow-hidden shadow-md"
            key={index}
          >
            <img
              className="w-full h-56 object-cover custom-image-width"
              src={`http://localhost:3000/${course.image}`} // ajout de guillemets autour de l'URL
              alt={course.title}
            />
            <div className="px-6 py-4">
              <div className="font-bold text-xl mb-2">{course.title}</div>
              <p className="text-gray-700 text-base">{course.description}</p>
            </div>
            <div className="mt-auto">
              {getItemQuantity(course.course_id) === 0 ? (
                <button
                  onClick={() => {
                    increaseCartQuantity(course.course_id, course.price, props.id);
                  }}
                  className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                >
                  Add To Cart
                </button>
              ) : (
                <div className="flex flex-col items-center gap-0.5">
                  <div className="flex items-center justify-center gap-0.5"></div>
                  {course.isPurchase ? null : ( // ajout de la condition pour masquer le bouton Remove si isPurchase est égal à true
                    <button
                      onClick={() => {
                        removeFromCart(course.course_id);
                      }}
                      className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded-sm"
                    >
                      Remove
                    </button>
                  )}
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </Fragment>
  );
}

export default OurService;
