import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Fragment } from 'react';

function Services() {
    const [tableauData, setTableauData] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3000/training-courses')
        .then(response => setTableauData(response.data))
        .catch(err => console.error(err));
    }, []
    );
  return (
<Fragment>
    <div class="flex justify-center items-center h-16 bg-gray-100 text-gray-900 font-bold text-lg uppercase tracking-wide">
      OUR SERVICES
    </div>
    <div className="flex flex-wrap justify-center">
      {tableauData.map((course, index) => (
        <div className="max-w-sm mx-4 my-4 rounded overflow-hidden shadow-lg" key={index}>
            <img className="w-full" src={`http://localhost:3000/${course.image}`} alt={course.title} />
            <d    iv className="px-6 py-4">
                <div className="font-bold text-xl mb-2">{course.title}</div>
                <p className="text-gray-700 text-base">{course.description}</p>
            </d>
        </div>
      ))}
    </div>
    </Fragment>
  );
}

export default Services;
