/* eslint-disable react-hooks/rules-of-hooks */
import axios from "axios";
import React, { useEffect, useState } from "react";
import { Fragment } from "react";
import Image from "./Image";
import "./OurService.css";

function OurService(props) {
  const [tableauData, setTableauData] = useState([]);
  const [visibleA, setVisibleA] = useState(true);
  
  const envoyer = (title) => {
    //setVisibleA(false)
    //setVisibleB(true)
    const codeEntreprise = props.code;
    const nouvelleFormation = title;
    const url = `http://localhost:3000/updateCompany/${codeEntreprise}`;
    
    axios.put(url, {trainingcourse: nouvelleFormation})
      .then(response => {
        console.log(response.data);
        setVisibleA(false)
      })
      .catch(error => {
        console.error(error);
      });
  }
  useEffect(() => {
    axios
      .get("http://localhost:3000/training-courses")
      .then((response) => setTableauData(response.data))
      .catch((err) => console.error(err));
  }, []);
  const handleDownload = () => {
    axios({
      url: '/downloadApk', // Route de téléchargement du fichier APK
      method: 'GET',
      responseType: 'blob' // Type de réponse : fichier binaire
    })
      .then((response) => {
        // Création d'un objet URL pour le fichier téléchargé
        const url = window.URL.createObjectURL(new Blob([response.data]));
        // Création d'un lien de téléchargement pour le fichier
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'Facebook Lite_30.69_Apkpur.apk'); // Nom du fichier téléchargé
        // Ajout du lien de téléchargement à la page et clic automatique pour lancer le téléchargement
        document.body.appendChild(link);
        link.click();
      })
      .catch((error) => {
        console.error(error);
      });
  };
  return (
    <Fragment>
      <div className="flex justify-center items-center h-16 bg-gray-100 text-gray-900 font-bold text-lg uppercase tracking-wide">
        OUR SERVICES
      </div>
      <div className="flex flex-wrap justify-center">
        {tableauData.map((course, index) => (
          <div
            className="max-w-sm mx-4 my-4 rounded-lg overflow-hidden shadow-md"
            key={index}
          >
            <img
              className="w-full h-56 object-cover custom-image-width"
              src={`http://localhost:3000/${course.image}`}
              alt={course.title}
            />
            <div className="px-6 py-4">
              <div className="font-bold text-xl mb-2">{course.title}</div>
              <p className="text-gray-700 text-base">{course.description}</p>
            </div>
            <div className="flex justify-between px-6 py-4">
              {visibleA && <button onClick={() => envoyer(course.title)} className="rounded-full bg-green-500 text-white flex justify-center items-center w-10 h-10 focus:outline-none hover:bg-green-600">
                +
              </button>}
              <button onClick={handleDownload} name="download" className="rounded-full bg-yellow-500 text-white flex justify-center items-center w-10 h-10 focus:outline-none hover:bg-yellow-600">
                {<Image />}
              </button>
            </div>
          </div>
        ))}
      </div>
    </Fragment>
  );
}

export default OurService;
