import { useState } from 'react';

function EditInformations() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');


  function handleUsernameChange(event) {
    setUsername(event.target.value);
  }

  function handleEmailChange(event) {
    setEmail(event.target.value);
  }

  function handleSave() {
    // Code pour enregistrer les donnÃ©es du formulaire
  }

  function handleCancel() {
    // Code pour annuler l'enregistrement et vider le formulaire
  }
  const [image, setImage] = useState(null);
  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };
  return (
    <form className="flex flex-col space-y-6 border p-4 bg-white mr-5 mt-20">
      <div className='mr-60 ml-10 '>
      <div className="flex items-center space-x-4">
        <label htmlFor="image" className="font-medium text-gray-700">
          Image
        </label>
        <div className="relative">
        <input
        type="file"
        id="image"
        name="image"
        accept="image/*"
        onChange={handleImageChange}
        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
          <div className="absolute inset-0 cursor-pointer">
            <div className="h-full w-full bg-gray-100 opacity-0 hover:opacity-30 rounded-md"></div>
            <div className="absolute inset-0 flex items-center justify-center">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6 text-gray-500"
                viewBox="0 0 20 20"
                fill="currentColor"
              >
                <path
                  fillRule="evenodd"
                  d="M17 8v7a2 2 0 01-2 2H5a2 2 0 01-2-2V8h2v7h10V8h2zm-9-6a2 2 0 012 2v2a2 2 0 11-4 0V4a2 2 0 012-2zM9 7a1 1 0 011 1v5a1 1 0 11-2 0V8a1 1 0 011-1zM3 8v7a4 4 0 004 4h6a4 4 0 004-4V8a2 2 0 00-2-2H5a2 2 0 00-2 2z"
                  clipRule="evenodd"
                />
              </svg>
            </div>
          </div>
        </div>
      </div>
      <div className='w-80'>
        <label htmlFor="username" className="font-medium text-gray-700">
          Username
        </label>
        <input
            id="username"
            name="username"
            type="username"
            className="border-gray-300 px-4 py-2 w-full shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent border-gray-300 text-gray-600 placeholder-gray-400 border-b-2 border-black"
            placeholder="Username"
            onChange={handleUsernameChange}
            value={username}
        />
      </div>
      <div>
        <label htmlFor="email" className="font-medium text-gray-700">
          Email
        </label>
        <input
            id="email"
            name="email"
            type="email"
            className="border-gray-300 px-4 py-2 w-full shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent border-gray-300 text-gray-600 placeholder-gray-400 border-b-2 border-black"
            placeholder="Email address"
            onChange={handleEmailChange}
            value={email}
        />
      </div>
      </div>
      <div className="flex justify-end">
        <button
          type="button"
          className="px-4 py-2 mr-2 border border-transparent text-base font-medium rounded-md text-white bg-indigo-500 hover:bg-indigo-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          onClick={handleSave}
        >
          Enregistrer
        </button>
        <button
          type="button"
          className="px-4 py-2  border border-transparent text-base font-medium rounded-md text-red-700 bg-red-200 hover:bg-red-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          onClick={handleCancel}
        >
          Annuler
        </button>
      </div>
    </form>
    );
}

export default EditInformations;
