import React, { useState } from 'react'
import EditInformations from './EditInformations'
import EditAdminProfile from './EditAdminProfile';

const Edit = () => {
    const [switchComponent,setSwitchComponent]=useState(null);
    const handleGeneral = ()=>{
        setSwitchComponent(false)
    }
    const handlePassword = ()=>{
        setSwitchComponent(true)
    }
  return (
    <div class="bg3  flex justify-between">
        <div class=" text-3xl  text-white ml-10 mt-20 absolute">Settings</div>
        <div className='flex items-center mt-2 mr-20 ml-10 mb-40'>
            <div className="border rounded bg-white p-2 flex flex-col">
                <button onClick={handleGeneral} className="bg-pink-400 hover:bg-pink-500 text-white font-bold py-2 px-4 rounded mb-2">
                    General
                </button>
                <button on className="bg-pink-400 hover:bg-pink-500 text-white font-bold py-2 px-4 rounded">
                    Change password
                </button>
            </div>
        </div>

        <div className='ml-10 mt-20'>
            <div>
                {!switchComponent ? <EditInformations/> : <EditAdminProfile/>}
            </div>
        </div>
    </div>

  )
}

export default Edit
