import axios from 'axios';
import React, { useEffect, useState } from 'react';

function Services() {
    const [tableauData, setTableauData] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3000/training-courses')
        .then(response => setTableauData(response.data))
        .catch(err => console.error(err));
    }, []
    );
  return (
    <div className="flex flex-wrap justify-center">
      {tableauData.map((course, index) => (
        <div className="max-w-sm w-full md:w-1/2 lg:w-1/3 mx-2 my-2 p-2 rounded-lg shadow-lg bg-white" key={index}>
          <img className="w-full h-48 object-cover rounded-t-lg" src={`http://localhost:3000/${course.image}`} alt={course.title} />
          <div className="px-4 py-2">
            <h2 className="text-lg font-bold text-gray-800">{course.title}</h2>
            <p className="text-gray-700 text-base mt-2">{course.description}</p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Services;
