import React, { useState, useEffect } from "react";
import { useShoppingCart } from "./context/ShoppingCartContext";
import FormatCurrency from "./FormatCurrency";
import axios from "axios";

const CartItem = ({ id, quantity, company_id }) => {
  const { removeFromCart } = useShoppingCart();
  const [item, setItem] = useState(null);
  const [isPurchased, setIsPurchased] = useState(false);
  const [isAlreadyPurchased, setIsAlreadyPurchased] = useState(false);

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await fetch(
          `http://localhost:3000/training-courses/${id}`
        );
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setItem(data);
        setIsAlreadyPurchased(data.isPurchased); // Mettre à jour l'état d'achat de la formation
      } catch (error) {
        console.error(error);
      }
    };
    fetchItem();
  }, [id]);
  const envoyer = (index) => {
    const companyId = company_id;
    const courseId = index;
    axios
      .post(
        `http://localhost:3000/company/${companyId}/cours/${courseId}`,
      )
      .then((response) => {
        console.log(response.data); // afficher le message de succès envoyé par le serveur
        setIsAlreadyPurchased(true); // mettre à jour l'état isAlreadyPurchased
        setIsPurchased(true); // mettre à jour l'état d'achat
      })
      .catch((error) => {
        console.error(error); // afficher les erreurs dans la console
      });
  };

  if (item == null) return null;

  return (
    <div className="flex gap-2 items-center">
      <img
        src={`http://localhost:3000/${item.image}`}
        alt="cart-img"
        style={{ width: "125px", height: "75px", objectFit: "cover" }}
      />
      <div className="flex-grow">
        <div className="text-lg">{item.title} </div>
        <div className="text-gray-400 text-sm">{item.price}</div>
      </div>
      <div>{FormatCurrency(item.price * quantity)}</div>
      <button
        className="text-red-500 bg-transparent border border-red-500 rounded hover:bg-red-500 hover:text-white focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
        onClick={() => removeFromCart(item.course_id)}
      >
        &times;
      </button>
      <button
      className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
      onClick={() => {
        setIsPurchased(true);
        fetch(`http://localhost:3000/company/${company_id}/cours/${item.course_id}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            isPurchased: true
          })
        }).then(response => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          console.log('Achat effectué avec succès');
        }).catch(error => {
          console.error('Erreur lors de l\'envoi de la requête POST :', error);
        });
      }}
    >
      Acheter
    </button>
    
    </div>
  );
};

export default CartItem;
