import axios from "axios";
import React, { useEffect, useState } from "react";
import { Fragment } from "react";

function Services() {
  const [tableauData, setTableauData] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3000/training-courses")
      .then((response) => setTableauData(response.data))
      .catch((err) => console.error(err));
  }, []);
  return (
    <Fragment>
      <div className="flex justify-center items-center h-16 bg-gray-100 text-gray-900 font-bold text-lg uppercase tracking-wide">
        OUR SERVICES
      </div>
      <div className="flex flex-wrap justify-center">
        {tableauData.map((course, index) => (
          <div class="max-w-sm mx-auto my-4 rounded-lg overflow-hidden shadow-md">
            <img
              class="w-full h-56 object-cover"
              src={`http://localhost:3000/${course.image}`}
              alt={course.title}
            />
            <div class="px-6 py-4">
              <h2 class="font-bold text-2xl mb-2">{course.title}</h2>
              <p class="text-gray-700 text-lg mb-4">{course.description}</p>
              <p class="text-lg font-bold">{course.price}</p>
              <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mt-4">
                Add to Cart
              </button>
              <button
                onClick={handleDownload}
                name="download"
                className="rounded-full bg-yellow-500 text-white flex justify-center items-center w-10 h-10 focus:outline-none hover:bg-yellow-600"
              >
                {<DownloadImage />}
              </button>
            </div>
          </div>
        ))}
      </div>
    </Fragment>
  );
}

export default Services;
