import Dashboard from "./components/Dashboard";
import { Routes, Route, BrowserRouter, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Parent from "./components/Parent";
import Client from "./components/client/Client";
import Account from "./components/Account";
import Profile from "./components/profilePage/Profile";
import CompanyDetail from "./components/CompanyDetails";
import PageNotFound from "./components/PageNotFound/PageNotFound";
import ShoppingCartProvider from "./components/context/ShoppingCartContext";
import EditCourse from "./components/formation/EditCourse";
import Formation from "./components/formation/Formation";
import { Fragment, useState } from "react";
import ProtectedRoute from "./components/ProtectedRoute";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (email, password) => {
    isAuthenticated(true);
    setEmail(email);
    setPassword(password);
  };


  const handleLogout = () => {
    setIsLoggedIn(false);
    setEmail('');
    setPassword('');
    localStorage.removeItem('token');
    localStorage.removeItem('email');
  };

  return (
    <Fragment>
      <BrowserRouter>
        <ShoppingCartProvider>
          <Routes>
            <Route path="/" element={<Client />} />
            <Route path="/signIn" element={<Login onLogin={handleLogin} />} />
            <Route
              path="/profile"
              element={
                <ProtectedRoute isAuthenticated={isAuthenticated}>
                  <Profile />
                </ProtectedRoute>
              }
            />
            <Route
              path="/dashboard"
              element={
                <ProtectedRoute isAuthenticated={isAuthenticated}>
                  <Dashboard />
                </ProtectedRoute>
              }
            >
              <Route index element={<Formation />} />
              <Route path="account" element={<Account />} />
              <Route path="tableau" element={<Parent />} />
              <Route path="companyDetails/:id" element={<CompanyDetail />} />
              <Route path="edit-course/:id" element={<EditCourse />} />
              <Route path="formation" element={<Formation />}>
                <Route path="tableau" element={<Parent />} />
              </Route>
            </Route>
            <Route path="pageNotFound" element={<PageNotFound />} />
            <Route path="*" element={<Navigate to="/pageNotFound" />} />
          </Routes>
        </ShoppingCartProvider>
      </BrowserRouter>
    </Fragment>
  );
};
export default App;
