import React, { useState } from 'react';
import axios from 'axios';

const ContactForm = () => {
    const [formData, setFormData] = useState({
        email: '',
        phone: '',
        companyName: '',
        code: '',
        message: ''
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          const res = await axios.post('/submit-company', formData);
          console.log(res.data);
        } catch (error) {
          console.error(error);
        }
    };      

    const handleChange = (e) => {
    setFormData({
        ...formData,
        [e.target.name]: e.target.value
        });
    };

    return (
        <form className="max-w-lg mx-auto" onSubmit={handleSubmit}>
        <div className="mb-4 my-10">
        <label htmlFor="email" className="block text-gray-700 font-bold mb-2">
        Email
        </label>
        <input
            type="email"
            id="email"
            name="email"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            onChange={handleChange}
            value={formData.email}
            required
            />
        </div>
        <div className="mb-4">
        <label htmlFor="phone" className="block text-gray-700 font-bold mb-2">
        Phone
        </label>
        <input
            type="tel"
            id="phone"
            name="phone"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            onChange={handleChange}
            value={formData.phone}
            required
            />
        </div>
        <div className="mb-4">
        <label htmlFor="companyName" className="block text-gray-700 font-bold mb-2">
        Company Name
        </label>
        <input
            type="text"
            id="companyName"
            name="companyName"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            onChange={handleChange}
            value={formData.companyName}
            required
            />
        </div>
        <div className="mb-4">
        <label htmlFor="code" className="block text-gray-700 font-bold mb-2">
        Code
        </label>
        <input
            type="text"
            id="code"
            name="code"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            onChange={handleChange}
            value={formData.code}
            required
            />
        </div>
        <div className="mb-4">
        <label htmlFor="message" className="block text-gray-700 font-bold mb-2">
        Message
        </label>
        <textarea
            id="message"
            name="message"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            rows="5"
            onChange={handleChange}
            value={formData.message}
            required
            ></textarea>
        </div>
        <div className="flex justify-center">
        <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
        Submit
        </button>
        </div>
    </form>
    );
};
export default ContactForm
