import axios from "axios";
import React, { useEffect, useState } from "react";
import Navbar2 from "./Navbar2";
import OurService from "./OurService";

const Profile = () => {
  const [code, setCode] = useState("");
  const [id, setId] = useState(0);
  const email = localStorage.getItem("email");
  const [tableau, setTableau] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:3000/company/${email}`)
      .then((response) => {
        const company = response.data[0];
        setCode(company.code);
        setId(company.company_id)
      })
      .catch((error) => {
        console.error(error);
      });
    axios
    .get("http://localhost:3000/training-courses")
    .then((response) => setTableau(response.data))
    .catch((err) => console.error(err));
  }, [email]);

  return (
    <div>
      <Navbar2 code={code} />
      {code ? <OurService code={code} id={id} idCourse = {tableau.map(item=>item.course_id)}/> : null}
    </div>
  );
};

export default Profile;
