/* eslint-disable react-hooks/rules-of-hooks */
import axios from "axios";
import React, { useEffect, useState } from "react";
import { Fragment } from "react";
import { useShoppingCart } from "../context/ShoppingCartContext";
import "./OurService.css";
import { CompanyContext } from "./Profile";
import { useContext } from 'react';
function OurService(props) {
  const [tableauData, setTableauData] = useState([]);
  const [tableauDataB, setTableauDataB] = useState([]);
  const {getItemQuantity, increaseCartQuantity, removeFromCart} = useShoppingCart();
  const context = useContext()
  useEffect(() => {
    axios
      .get("http://localhost:3000/training-courses")
      .then((response) => setTableauData(response.data))
      .catch((err) => console.error(err));
  }, []);
  //on peut utiliser pour cacher remove button
  useEffect(() => {
    <CompanyContext.Consumer>
    {
      (id)=>{
      axios
      .get(`http://localhost:3000/companyB/${id}`)
      .then((response) => setTableauDataB(response.data))
      .catch((err) => console.error(err))
      }
    }
    </CompanyContext.Consumer>
  }, [props.id]);
  const ispurchased = tableauDataB.map(item=>item.ispurchased);
  //
  return (
    <Fragment>
      <div className="flex justify-center items-center h-16 bg-gray-100 text-gray-900 font-bold text-lg uppercase tracking-wide">
        OUR SERVICES
      </div>
      <div className="flex flex-wrap justify-center">
        {tableauData.map((course, index) => (
          <div
            className="max-w-sm mx-4 my-4 rounded-lg overflow-hidden shadow-md"
            key={index}
          >
            <img
              className="w-full h-56 object-cover custom-image-width"
              src={`http://localhost:3000/${course.image}`}
              alt={course.title}
            />
            <div className="px-6 py-4">
            <div className="flex">
              <div className="flex-1 font-bold text-xl mb-2">{course.title}</div>
              <div className="font-bold text-xl mb-2">{course.price}{' tnd'}</div>
            </div>
              <p className="text-gray-700 text-base">{course.description}</p>
            </div>
            
            <div className="mt-auto">
              {getItemQuantity(course.course_id) === 0 ? (
                <button onClick={()=>{increaseCartQuantity(course.course_id,course.price,props.id)}} className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                  Add To Cart
                </button>
                  ) : (
                    <div className="flex flex-col items-center gap-0.5">
                      <div className="flex items-center justify-center gap-0.5">
                      </div>
                      {!ispurchased && <button onClick={()=>{removeFromCart(course.course_id)}} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded-sm">
                        Remove
                      </button>}
                    </div>
                  )}
            </div>
          </div>
        ))}
      </div>
    </Fragment>
  );
}

export default OurService;
