import React, { useState } from "react";
import { useForm } from "react-hook-form";

function EditProfile() {
  const { register, handleSubmit, errors } = useForm();
  const [successMessage, setSuccessMessage] = useState("");

  const onSubmit = (data) => {
    // Soumettre les données du formulaire au backend pour changer le mot de passe
    console.log(data);
    setSuccessMessage("Mot de passe changé avec succès !");
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <form
        className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4"
        onSubmit={handleSubmit(onSubmit)}
      >
        <div className="mb-4">
          <label
            className="block text-gray-700 font-bold mb-2"
            htmlFor="oldPassword"
          >
            Ancien mot de passe
          </label>
          <input
            className={`appearance-none border ${
              errors.oldPassword ? "border-red-500" : "border-gray-400"
            } rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline`}
            id="oldPassword"
            type="password"
            placeholder="********"
            name="oldPassword"
            ref={register({ required: true })}
          />
          {errors.oldPassword && (
            <p className="text-red-500 text-xs italic">
              Le champ Ancien mot de passe est requis.
            </p>
          )}
        </div>

        <div className="mb-4">
          <label
            className="block text-gray-700 font-bold mb-2"
            htmlFor="newPassword"
          >
            Nouveau mot de passe
          </label>
          <input
            className={`appearance-none border ${
              errors.newPassword ? "border-red-500" : "border-gray-400"
            } rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline`}
            id="newPassword"
            type="password"
            placeholder="********"
            name="newPassword"
            ref={register({
              required: true,
              minLength: 8,
            })}
          />
        </div>

        <div className="mb-6">
          <label
            className="block text-gray-700 font-bold mb-2"
            htmlFor="confirmPassword"
          >
            Confirmer le nouveau mot de passe
          </label>
          <input
            className={`appearance-none border ${
              errors.confirmPassword ? "border-red-500" : "border-gray-400"
            } rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline`}
            id="confirmPassword"
            type="password"
            placeholder="********"
            name="confirmPassword"
            ref={register({
              required: true,
            })}
          />
        </div>
      </form>
    </div>
  );
}
export default EditProfile;
