import React, { useState } from 'react';
import axios from 'axios';

function Formation() {
  const [idFormation, setIdFormation] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [apkFile, setApkFile] = useState(null);


  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('idFormation', idFormation);
    formData.append('title', title);
    formData.append('description', description);
    formData.append('image', image);
    formData.append('apkFile', apkFile);

    try {
      await axios.post('http://localhost:3000/submit-form', formData);
      console.log('Form submitted successfully');
      // Clear form inputs
      setIdFormation('');
      setTitle('');
      setDescription('');
      setImage(null);
      setApkFile(null);
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setImage(selectedImage);
  };

  const handleApkFileChange = (e) => {
    const selectedApkFile = e.target.files[0];
    setApkFile(selectedApkFile);
  };

  return (
    <div className="max-w-md mx-auto my-8">
      <h2 className="text-2xl font-bold mb-4">Add a New Training Course</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="idFormation" className="block text-gray-700 font-bold mb-2">
            Formation ID
          </label>
          <input
            id="idFormation"
            type="text"
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            value={idFormation}
            onChange={(e) => setIdFormation(e.target.value)}
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="title" className="block text-gray-700 font-bold mb-2">
            Title
          </label>
          <input
            id="title"
            type="text"
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="description" className="block text-gray-700 font-bold mb-2">
            Description
          </label>
          <textarea
            id="description"
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
          </div>
          <div className="mb-4">
          <label htmlFor="image" className="block text-gray-700 font-bold mb-2">
          Image
          </label>
          <input
                   id="image"
                   type="file"
                   accept="image/*"
                   className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                   onChange={handleImageChange}
                   required
                 />
          </div>
          <div className="mb-4">
          <label htmlFor="apkFile" className="block text-gray-700 font-bold mb-2">
          APK File
          </label>
          <input
                   id="apkFile"
                   type="file"
                   accept=".apk"
                   className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                   onChange={handleApkFileChange}
                   required
                 />
          </div>
          <button oncl
                 type="submit"
                 className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
               >
            Add Course
          </button>
          </form>
        </div>
    );
}
export default Formation;
