// Tableau.js
import React from "react";
import { useNavigate } from "react-router-dom";

function Tableau({ tab }) {
    const navigate = useNavigate();
    const goToDashboard = ()=>{
        navigate('/dashboard');
    }
    const handleEdit = (id) => {
        // handle edit logic here
    }

    const handleDelete = (id) => {
        // handle delete logic here
    }
  return (
        <div className="max-w-md mx-auto my-8">
        <h2 className="text-2xl font-bold mb-4">Training Course Table</h2>
        <table className="table-auto">
            <thead>
            <tr>
                <th className="px-4 py-2">ID</th>
                <th className="px-4 py-2">Title</th>
                <th className="px-4 py-2">Description</th>
                <th className="px-4 py-2">Image</th>
                <th className="px-4 py-2">APK File</th>
            </tr>
            </thead>
            <tbody>
            {tab.map((row, index) => (
                <tr key={index}>
                <td className="border px-4 py-2">{row.id_formation}</td>
                <td className="border px-4 py-2">{row.title}</td>
                <td className="border px-4 py-2">{row.description}</td>
                <td className="border px-4 py-2">
                    <img src={`/uploads/${row.image}`} alt={row.title} width="50" />
                </td>
                <td className="border px-4 py-2">{row.apk_file.type}</td>
                </tr>
            ))}
            </tbody>
        </table>
        <div className="py-2"></div>
        <button onClick={goToDashboard}
                    type="button"
                    className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                Go to Dashboard page
        </button>
    </div>
  );
}

export default Tableau;
