/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from 'react';
import axios from 'axios';

const EditProfile = () => {
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");
  
  const email = localStorage.getItem("email");

  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage("");
    axios.post("http://localhost:3000/changer-mot-de-passe", { ancien_mot_de_passe: oldPassword, nouveau_mot_de_passe: newPassword, confirmation_mot_de_passe: confirmPassword, email: email })
      .then((response) => {
  const shouldLogout = window.confirm("Password updated successfully. Would you like to logout?");
  if (shouldLogout) {
    localStorage.clear();
    window.location.href = "/signIn";
  }
  setMessage(
    <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded relative" role="alert">
      <strong className="font-bold">Password updated successfully!</strong>
      <span className="block sm:inline">You will now be logged out.</span>
      <span className="absolute top-0 bottom-0 right-0 px-4 py-3">
        <svg className="fill-current h-6 w-6 text-green-500" role="button" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><title>Close</title><path d="M14.35 5.65a1 1 0 0 0-1.41 0L10 8.59l-2.94-2.94a1 1 0 0 0-1.41 1.41L8.59 10l-2.94 2.94a1 1 0 0 0 1.41 1.41L10 11.41l2.94 2.94a1 1 0 0 0 1.41-1.41L11.41 10l2.94-2.94a1 1 0 0 0 0-1.41z"/></svg>
      </span>
    </div>
  );
})

  }
  const handleReset = () => {
    setOldPassword("");
    setNewPassword("");
    setConfirmPassword("");
    setMessage("");
  }
  return (
    <div className="max-w-md mx-auto mt-8">
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="ancien_mot_de_passe" className="block text-gray-700 font-bold mb-2">
            Ancien mot de passe :
          </label>
          <input 
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
            id="ancien_mot_de_passe" 
            type="password" 
            name="ancien_mot_de_passe" 
            required 
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label htmlFor="nouveau_mot_de_passe" className="block text-gray-700 font-bold mb-2">
            Nouveau mot de passe :
          </label>
          <input 
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
            id="nouveau_mot_de_passe" 
            type="password" 
            name="nouveau_mot_de_passe" 
            required 
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
        </div>
        <div className="mb-6">
          <label htmlFor="confirmation_mot_de_passe" className="block text-gray-700 font-bold mb-2">
            Confirmation du nouveau mot de passe :
          </label>
          <input 
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
            id="confirmation_mot_de_passe" 
            type="password" 
            name="confirmation_mot_de_passe" 
            required 
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </div>
        {message && <p className="text-red-500">{message}</p>}
        <div className="flex items-center justify-between">
          <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="submit">
          Enregistrer
          </button>
          <button className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="button" onClick={handleReset}>
          Annuler
          </button>
          </div>
        </form>
      </div>
    );
  };
          
export default EditProfile;
