import React from "react";
import { XIcon } from '@heroicons/react/solid';

import { useShoppingCart } from "./context/ShoppingCartContext";

const ShoppingCart = ({ isOpen }) => {
  const { closeCart } = useShoppingCart();
  return (
    <div className={`fixed inset-0 overflow-hidden z-50 ${isOpen ? 'block' : 'hidden'}`}>
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute inset-0 bg-gray-500 bg-opacity-75 transition-opacity" aria-hidden="true"></div>
        <section className="absolute inset-y-0 right-0 pl-10 max-w-full flex sm:pl-16 outline-none" aria-labelledby="slide-over-heading">
          <div className="w-screen max-w-md">
            <div className="h-full flex flex-col py-6 bg-white shadow-xl overflow-y-scroll">
              <div className="px-4 sm:px-6">
                <div className="flex items-start justify-between">
                  <h2 className="text-lg font-medium text-gray-900" id="slide-over-heading">
                    Cart
                  </h2>
                  <div className="ml-3 h-7 flex items-center">
                    <button onClick={closeCart} className="bg-white rounded-md text-gray-400 hover:text-gray-500 focus:ring-2 focus:ring-indigo-500">
                      <span className="sr-only">Close panel</span>
                      <XIcon className="h-6 w-6" aria-hidden="true" />
                    </button>
                  </div>
                </div>
              </div>
              <div className="mt-6 relative flex-1 px-4 sm:px-6">
                {/* CONTENT */}
              </div>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};
export default ShoppingCart;
