import React, { createContext, useContext, useState, useEffect } from "react";

const ShoppingCartContext = createContext();

const ShoppingCart = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const addToCart = (itemId) => {
    setCartItems([...cartItems, { id: itemId, quantity: 1 }]);
  };

  const removeFromCart = (itemId) => {
    setCartItems(cartItems.filter((item) => item.id !== itemId));
  };

  const closeCart = () => {
    // close cart
  };

  useEffect(() => {
    const fetchItems = async () => {
      setIsLoading(true);

      const itemPromises = cartItems.map(async (item) => {
        const response = await fetch(`http://localhost:3000/training-courses/${item.id}`);
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        return { ...data, price: data.price };
      });

      const items = await Promise.all(itemPromises);

      setIsLoading(false);

      setCartItems(
        items.map((item, index) => ({
          ...item,
          quantity: cartItems[index].quantity,
        }))
      );
    };

    if (cartItems.length > 0) {
      fetchItems();
    }
  }, [cartItems]);

  const contextValue = { cartItems, addToCart, removeFromCart, closeCart, isLoading };
  return (
    <ShoppingCartContext.Provider value={contextValue}>
      {children}
    </ShoppingCartContext.Provider>
  );
};

const useShoppingCart = () => useContext(ShoppingCartContext);

export { ShoppingCart, useShoppingCart };
