/* eslint-disable react-hooks/rules-of-hooks */
import axios from "axios";
import React, { useEffect, useState } from "react";
import { Fragment } from "react";
import { useShoppingCart } from "../context/ShoppingCartContext";
import "./OurService.css";

function OurService(props) {
  const [tableauData, setTableauData] = useState([]);
  const {getItemQuantity, increaseCartQuantity} = useShoppingCart();
  const [courseId,setCourseId] = useState(0)
  const quantity = getItemQuantity(courseId);
  
/*   const envoyer = (index) => {
    console.log(index)
    console.log(props.id)
    axios.post(`http://localhost:3000/company/${props.id}/cours/${index}`)
      .then(response => {
        console.log(response.data); // afficher le message de succès envoyé par le serveur
      })
      .catch(error => {
        console.error(error); // afficher les erreurs dans la console
      });
  }; */

  useEffect(() => {
    axios
      .get("http://localhost:3000/training-courses")
      .then((response) => setTableauData(response.data))
      .catch((err) => console.error(err));
  }, []);

/*   const handleDownload = () => {
    axios({
      url: "http://localhost:3000/downloadApk", // Route de téléchargement du fichier APK
      method: "GET",
      responseType: "blob", // Type de réponse : fichier binaire
    })
      .then((response) => {
        // Création d'un objet URL pour le fichier téléchargé
        const url = window.URL.createObjectURL(new Blob([response.data]));
        // Création d'un lien de téléchargement pour le fichier
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", "Facebook Lite_30.69_Apkpur.apk"); // Nom du fichier téléchargé
        // Ajout du lien de téléchargement à la page et clic automatique pour lancer le téléchargement
        document.body.appendChild(link);
        link.click();
      })
      .catch((error) => {
        console.error(error);
      });
  }; */

  return (
    <Fragment>
      <div className="flex justify-center items-center h-16 bg-gray-100 text-gray-900 font-bold text-lg uppercase tracking-wide">
        OUR SERVICES
      </div>
      <div className="flex flex-wrap justify-center">
        {tableauData.map((course, index) => (
          <div
            className="max-w-sm mx-4 my-4 rounded-lg overflow-hidden shadow-md"
            key={index}
          >
            <img
              className="w-full h-56 object-cover custom-image-width"
              src={`http://localhost:3000/${course.image}`}
              alt={course.title}
            />
            <div className="px-6 py-4">
              <div className="font-bold text-xl mb-2">{course.title}</div>
              <p className="text-gray-700 text-base">{course.description}</p>
            </div>
            
            <div class="mt-auto">
              {quantity === 0 ? (
                <button onClick={()=>{increaseCartQuantity(course.course_id)}} class="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                  Add To Cart
                </button>
                  ) : (
                    <div class="flex flex-col items-center gap-0.5">
                      <div class="flex items-center justify-center gap-0.5">
                        <div>
                          <span class="text-lg font-semibold">{quantity} in cart</span>
                        </div>
                      </div>
                      <button onClick={()=>{removeFromCart}} class="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded-sm">
                        Remove
                      </button>
                      <div>{setCourseId(course.course_id)}</div>
                    </div>
                  )}
                </div>
            </div>
          ))}
      </div>
    </Fragment>
  );
}

export default OurService;
