/* eslint-disable jsx-a11y/alt-text */
import React from 'react';
import PropTypes from 'prop-types';
import { useState } from 'react';

const SideBar = ({ activeComponent, onMenuItemClick }) => {
  const [open, setOpen] = useState(true);
  const Menus = [
    { title: 'Dashboard', src: 'Chart_fill' },
    { title: 'Accounts', src: 'User', gap: true },
    { title: 'Formation', src: 'Formation' },
    { title: 'Logout', src: 'Logout', gap: true },
  ];

  const handleMenuItemClick = (component) => {
    onMenuItemClick(component);
  };

  return (
    <div>
      <div
        className={` ${
          open ? 'w-72' : 'w-20 '
        } bg-dark-purple h-screen p-5  pt-8 relative duration-300`}
      >
        <img
          src="./assets/control.png"
          className={`absolute cursor-pointer -right-3 top-9 w-7 border-dark-purple
                    border-2 rounded-full  ${!open && 'rotate-180'}`}
          onClick={() => setOpen(!open)}
        />
        <div className="flex gap-x-4 items-center">
          <img
            src="./assets/logo.png"
            className={`cursor-pointer duration-500 ${
              open && 'rotate-[360deg]'
            }`}
          />
          <h1
            className={`text-white origin-left font-medium text-xl duration-200 ${
              !open && 'scale-0'
            }`}
          >
            Tadreex
          </h1>
        </div>
        <ul className="pt-6">
          {Menus.map((menu, index) => (
            <li
              key={index}
              className={`flex  rounded-md p-2 cursor-pointer hover:bg-light-white text-gray-300 text-sm items-center gap-x-4 
                        ${menu.gap ? 'mt-9' : 'mt-2'} ${
                activeComponent === menu.title && 'bg-light-white'
              } `}
              onClick={() => handleMenuItemClick(menu.title)}
            >
              <img src={`./assets/${menu.src}.png`} />
              <span
                className={`${!open && 'hidden'} origin-left duration-200`}
                >
                {menu.title}
                </span>
                </li>
                ))}
                </ul>
                </div>
                </div>
                );
                };
                
                SideBar.propTypes = {
                activeComponent: PropTypes.string.isRequired,
                onMenuItemClick: PropTypes.func.isRequired,
                }
                ;
                
export default SideBar;
