const CURRENCY_FORMATTER = new Intl.NumberFormat(undefined, {
    currency: "TND",
    style: "currency",
  });
  const FormatCurrency = (number) => {
    return CURRENCY_FORMATTER.format(number);
  };
  
  export default FormatCurrency;
