import React from "react";

function Cart(props) {
  return (
    <div className="bg-white p-4 rounded-lg shadow-md">
      <h2 className="text-lg font-bold mb-4">Panier</h2>
      {props.items.length === 0 && <p>Votre panier est vide.</p>}
      {props.items.map((item) => (
        <div key={item.id} className="flex items-center mb-2">
          <img src={item.image} alt={item.name} className="h-16 w-16 mr-4" />
          <div>
            <h3 className="font-bold">{item.name}</h3>
            <p className="text-gray-500">{item.price}€</p>
          </div>
        </div>
      ))}
      {props.items.length > 0 && (
        <div className="mt-4 text-right">
          <p className="font-bold">Total : {props.total}€</p>
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded-md mt-4"
            onClick={props.onCheckout}
          >
            Payer
          </button>
        </div>
      )}
    </div>
  );
}

export default Cart;
