import React from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';

function Tableau({ tab }) {
  const navigate = useNavigate();
  const goToDashboard = () => {
    navigate("/dashboard");
  };

  const handleEdit = (id) => {
    navigate(`/edit-course/${id}`);
  };
  

  const refreshPage = ()=>{
    window.location.reload();
    }
    const handleDelete = (id_formation) => {
      if (window.confirm("Are you sure you want to delete this course?")) {
        axios
          .delete(`http://localhost:3000/training-courses/${id_formation}`)
          .then((response) => {
            console.log("deleted");
            // Supprimer la ligne du tableau après avoir reçu une réponse positive du serveur
            refreshPage();
          })
          .catch((error) => {
            console.error(error);
            // Afficher une erreur à l'utilisateur
          });
      }
    };
    

  return (
    <div className="container mx-auto my-8">
      <h2 className="text-2xl font-bold mb-4">Training Course Table</h2>
      <table className="min-w-full divide-y divide-gray-200">
        <thead>
          <tr>
            <th className="px-6 py-3 bg-gray-50 text-left text-xs leading-4 font-medium text-gray-500 uppercase tracking-wider">
              Title
            </th>
            <th className="px-6 py-3 bg-gray-50 text-left text-xs leading-4 font-medium text-gray-500 uppercase tracking-wider">
              Description
            </th>
            <th className="px-6 py-3 bg-gray-50 text-left text-xs leading-4 font-medium text-gray-500 uppercase tracking-wider">
              Image
            </th>
            <th className="px-6 py-3 bg-gray-50 text-left text-xs leading-4 font-medium text-gray-500 uppercase tracking-wider">
              APK File
            </th>
            <th className="px-6 py-3 bg-gray-50"></th>
            <th className="px-6 py-3 bg-gray-50"></th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {tab.map((row, index) => (
            <tr key={index}>
              <td className="px-6 py-4 whitespace-no-wrap">{row.title}</td>
              <td className="px-6 py-4 whitespace-no-wrap">{row.description}</td>
              <td className="px-6 py-4 whitespace-no-wrap"> 
                <img src={`http://localhost:3000/${row.image}`} alt={row.title} width="50px" />
              </td>
              <td className="px-6 py-4 whitespace-no-wrap">{row.apk_file}</td>
              <td className="px-6 py-4 whitespace-no-wrap">
                <button
                  onClick={() => handleEdit(row.id_formation)}
                  className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  Edit
                </button>
              </td>
              <td className="px-6 py-4 whitespace-no-wrap">
                <button
                  onClick={() => handleDelete(row.id_formation)}
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="py-4"></div>
      <button
        onClick={goToDashboard}
        type="button"
        className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
                Go to Dashboard page
        </button>
    </div>
  );
}

export default Tableau;
