/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from 'react';
import axios from 'axios';

const EditAdminProfile = () => {
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");
  
  const email = localStorage.getItem("email");

  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage("");
    axios.post("http://localhost:3000/changer-password", { ancien_mot_de_passe: oldPassword, nouveau_mot_de_passe: newPassword, confirmation_mot_de_passe: confirmPassword, email: email })
      .then((response) => {
        setMessage(response.data);
        const shouldLogout = window.confirm("Password updated successfully. Would you like to logout?");
        if (shouldLogout) {
          localStorage.clear();
          window.location.href = "/signIn";
        }
      })
      .catch((error) => {
        setMessage(error.response.data);
      });
  }
  const handleReset = () => {
    setOldPassword("");
    setNewPassword("");
    setConfirmPassword("");
    setMessage("");
  }
  return (
    <div className="max-w-md mx-auto mt-8">
      <form onSubmit={handleSubmit}>
        <div className="text-3xl font-bold text-purpl-500 mb-4">
          Change password
        </div>
        <div className="mb-4">
          <label htmlFor="ancien_mot_de_passe" className="block text-gray-700 font-bold mb-2">
            Ancien mot de passe :
          </label>
          <input 
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
            id="ancien_mot_de_passe" 
            type="password" 
            name="ancien_mot_de_passe" 
            required 
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label htmlFor="nouveau_mot_de_passe" className="block text-gray-700 font-bold mb-2">
            Nouveau mot de passe :
          </label>
          <input 
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
            id="nouveau_mot_de_passe" 
            type="password" 
            name="nouveau_mot_de_passe" 
            required 
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
        </div>
        <div className="mb-6">
          <label htmlFor="confirmation_mot_de_passe" className="block text-gray-700 font-bold mb-2">
            Confirmation du nouveau mot de passe :
          </label>
          <input 
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
            id="confirmation_mot_de_passe" 
            type="password" 
            name="confirmation_mot_de_passe" 
            required 
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </div>
        {message && <p className="text-red-500">{message}</p>}
        <div className="flex items-center justify-between">
          <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="submit">
          Enregistrer
          </button>
          <button className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="button" onClick={handleReset}>
          Annuler
          </button>
          </div>
        </form>
      </div>
    );
  };
          
export default EditAdminProfile;
