import React, { useState } from 'react';
import axios from 'axios';
import { Dialog, Transition } from "@headlessui/react";
//traduction
import { useTranslation } from 'react-i18next';

const EditAdminProfile = () => {
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  //traduction 
  const {t} = useTranslation();

  const email = localStorage.getItem("email");
  console.log(email)
  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage("");
    axios.post("http://localhost:3000/changer-password", { ancien_mot_de_passe: oldPassword, nouveau_mot_de_passe: newPassword, confirmation_mot_de_passe: confirmPassword, email: email })
      .then((response) => {
        setMessage(response.data);
        setIsDialogOpen(true);
      })
      .catch((error) => {
        setMessage(error.response.data);
      });
  }
  
  const handleReset = () => {
    setOldPassword("");
    setNewPassword("");
    setConfirmPassword("");
    setMessage("");
  }
  return (
    <div class="relative h-screen bg-gray-100">
  <div class="absolute inset-0 bg-gradient-to-b from-blue-600 to-blue-400 z-0"></div>
  <div class="absolute inset-y-0 top-1/3 w-full h-2/3 bg-gray-200 z-0"></div>
  <div class="max-w-md mx-auto my-6 p-8 bg-white rounded-lg shadow-md z-10">
    <div className="mt-6 max-w-md mx-auto my-6 border-gray-500 border-solid rounded-lg pt-8 pb-8 pr-8 pl-8 bg-gray-100">
      <form onSubmit={handleSubmit}>
        <div className="text-3xl font-bold text-indigo-900 mb-4">
          {t('Change password')}
        </div>
        <div className="mb-4">
          <label className="block text-red-900 font-bold mb-2">
            {t('My')} email : {email}
          </label>
        </div>
        <div className="mb-4">
          <label htmlFor="ancien_mot_de_passe" className="block text-gray-700 font-bold mb-2">
            {t('Old password')} :
          </label>
          <input 
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
            id="ancien_mot_de_passe" 
            type="password" 
            name="ancien_mot_de_passe" 
            required 
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label htmlFor="nouveau_mot_de_passe" className="block text-gray-700 font-bold mb-2">
            {t('New password')} :
          </label>
          <input 
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
            id="nouveau_mot_de_passe" 
            type="password" 
            name="nouveau_mot_de_passe" 
            required 
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
        </div>
        <div className="mb-6">
          <label htmlFor="confirmation_mot_de_passe" className="block text-gray-700 font-bold mb-2">
            {t('Confirm the new password')} :
          </label>
          <input 
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
            id="confirmation_mot_de_passe" 
            type="password" 
            name="confirmation_mot_de_passe" 
            required 
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </div>
        {message && <p className="text-red-500">{message}</p>}
        <div className="flex items-center justify-between">
        <button 
                 className="bg-indigo-900 hover:bg-indigo-800 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" 
                 type="submit"
               >
          {t('Save password')}
        </button>
        <button 
                 className="bg-gray-400 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" 
                 type="button"
                 onClick={handleReset}
               >
        {t('Reset')}
        </button>
        </div>
        </form>
        <Transition show={isDialogOpen} as={React.Fragment}>
        <Dialog
          as="div"
          className="fixed inset-0 z-10 overflow-y-auto"
          onClose={() => setIsDialogOpen(false)}
        >
          <div className="min-h-screen px-4 text-center">
            <Dialog.Overlay className="fixed inset-0 bg-black opacity-30" />
    
            <div className="inline-block align-middle my-8 p-8 w-full max-w-md overflow-hidden text-left align-middle transition-all transform bg-white shadow-xl rounded">
              <Dialog.Title
                as="h3"
                className="text-lg font-medium leading-6 text-gray-900"
              >
                {t('Password successfully updated')}
              </Dialog.Title>
              <Dialog.Description className="text-sm mb-4">{t('Would you like to logout?')}</Dialog.Description>
              <div className="mt-4">
              <button className="bg-blue-900 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mr-2" onClick={() => {
                localStorage.clear();
                window.location.href = "/signIn";
              }}>
                {t('Yes')}
              </button>
              <button className="bg-gray-700 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" onClick={() => setIsDialogOpen(false)}>
                {t('No')}
              </button>
              </div>
            </div>
          </div>
        </Dialog>
      </Transition>
    </div>
    </div>
    /</div>
    );
  }
  
  export default EditAdminProfile;
