import React from 'react';

function Services() {
    
  const training_courses = [
    {
      title: "RV1",
      image: "http://localhost:3000/Diagramme de classe.jpg",
      description: "Description du cours RV1",
    },
    {
      title: "RV2",
      image: "http://localhost:3000/Diagramme de classe2.jpg",
      description: "Description du cours RV2",
    },
    {
      title: "RV3",
      image: "http://localhost:3000/Diagramme de classe3.jpg",
      description: "Description du cours RV3",
    },
  ];

  return (
    <div>
      {training_courses.map((course, index) => (
        <div className="card" key={index}>
          <h2>{course.title}</h2>
          <img src={course.image} alt={course.title} />
          <p>{course.description}</p>
        </div>
      ))}
    </div>
  );
}

export default Services;
