  {getItemQuantity(course.course_id) === 0 ? (
    <button onClick={()=>{increaseCartQuantity(course.course_id,course.price,id)}} className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
      Add To Cart
    </button>
      ) : (
        <div className="flex flex-col items-center gap-0.5">
          <div className="flex items-center justify-center gap-0.5">
          </div>
          {!ispurchased && (
            <button onClick={()=>{removeFromCart(course.course_id)}} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded-sm">
              Remove
            </button>
          )}
        </div>
      )}
</div>
{inProgress && (
  <button
    name="download"
    className="rounded-full bg-yellow-500 text-white flex justify-center items-center w-10 h-10 focus:outline-none hover:bg-yellow-600"
  >
    <img src="assets/download.png" />
  </button>
)}

    </Fragment>
  );
}

export default OurService;
