import React, { Fragment, useEffect, useState } from "react";
import axios from "axios";
import "tailwindcss/tailwind.css";
import { Dialog, Transition } from "@headlessui/react";
import { useNavigate } from 'react-router-dom';

const CompanyDetail = () => {
  const [companies, setCompanies] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [companyId, setCompanyId] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:3000/company")
      .then((response) => {
        setCompanies(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const handleClick = () => {
    axios
      .delete(`http://localhost:3000/company/${companyId}`)
      .then((response) => {
        console.log("deleted");
        setDeleted(1)
      })
      .catch((error) => {
        console.error(error);
      });
    setIsOpen(false);
  };

  const updateHandleClick = async (id_company) => {
    try {
      const response = await axios.get(`http://localhost:3000/check-company-by-code/${newCode}`);
      console.log(response.data);
      if (!response.data) {
        const response = await axios.put(
          `http://localhost:3000/update-company/${id_company}`,
          { code: newCode }
        );
        console.log("updated");
        setUpdated(1)
        console.log(response);
      } else {
        alert(`Company with code ${newCode} already exists.`);
      }
    } catch (error) {
      console.error(error);
    }
  };
  

  return (
    <table className="min-w-full divide-y divide-gray-200">
      <thead className="bg-gray-50">
        <tr>
          <th
            scope="col"
            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            Email
          </th>
          <th
            scope="col"
            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            Téléphone
          </th>
          <th
            scope="col"
            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            Nom de l'entreprise
          </th>
          <th
            scope="col"
            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            Message
          </th>
          <th
            scope="col"
            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            Code
          </th>
          <th
            scope="col"
            className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider"
          >
            Actions
          </th>
        </tr>
      </thead>
      <tbody className="bg-white divide-y divide-gray-200">
  {companies.map((company) => (
    (company.email !== "tadreex@gmail.com") &&
    <tr key={company.id}>
      <td className="px-6 py-4 whitespace-nowrap">
        <div className="text-sm text-gray-900">{company.email}</div>
      </td>
      <td className="px-6 py-4 whitespace-nowrap">
        <div className="text-sm text-gray-900">{company.phone}</div>
      </td>
      <td className="px-6 py-4 whitespace-nowrap">
        <div className="text-sm text-gray-900">{company.companyname}</div>
      </td>
      <td className="px-6 py-4 whitespace-nowrap">
        <div className="text-sm text-gray-900">{company.message}</div>
      </td>
      <td className="px-6 py-4 whitespace-nowrap">
        {company.code ? (
          <p>{company.code}</p>
        ) : (
          <input
            type="text"
            name="code"
            id="code"
            className="shadow-sm focus:ring-green-500 focus:border-green-500 block w-full sm:text-sm border-gray-300 rounded-md placeholder-gray-400 py-2 px-3 leading-tight focus:outline-none focus:shadow-outline-gray"
            placeholder="Code"
            value={newCode}
            required
            onChange={(event) => setNewCode(event.target.value)}
          />
        )}
      </td>
      <td className="px-6 py-4 whitespace-nowrap">
        <div className="flex items-center justify-center space-x-2">
          {company.code ? (
            <p className="text-green-500 font-bold">
              Code already generated
            </p>
          ) : (
            <button
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mr-2"
              onClick={() => updateHandleClick(company.id_company)}
            >
              Confirm
            </button>
          )}
          <button
            onClick={() => handleOpenDialog(company.id_company)}
            className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Delete
          </button>
          <button
            className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            onClick={() => handleDetail(company.id_company)}
            >
            Detail
          </button>
          <Transition appear show={isOpen} as={Fragment}>
            <Dialog
              as="div"
              className="fixed inset-0 z-10 overflow-y-auto"
              onClose={handleCloseDialog}
            >
              <div className="min-h-screen px-4 text-center">
                <Dialog.Overlay className="fixed inset-0 bg-black opacity-30" />
                <div className="inline-block align-middle bg-white rounded-lg px-8 py-8 text-left shadow-2xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full sm:p-6">
                  <div>
                    <div className="mt-3 text-center sm:mt-5">
                      <Dialog.Title
                        as="h3"
                        className="text-lg leading-6 font-medium text-gray-900"
                      >
                        Delete company account
                      </Dialog.Title>
                      <div className="mt-2">
                        <p className="text-sm leading-5 text-gray-500">
                                Are you sure you want to delete this company account ?
                              </p>
                            </div>
                          </div>
                          <div className="mt-5 sm:mt-6 flex justify-center">
                            <button
                              onClick={handleCloseDialog}
                              className="mr-4 inline-flex justify-center w-24 rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-gray-100 text-base font-medium text-gray-700 hover:text-gray-500 focus:outline-none focus:shadow-outline-blue focus:border-blue-300 sm:text-sm sm:leading-5"
                            >
                              Cancel
                            </button>
                            <button
                              onClick={handleClick}
                              className="inline-flex justify-center w-24 rounded-md border border-transparent shadow-sm px-4 py-2 bg-green-600 text-base font-medium text-white hover:bg-green-500 focus:outline-none focus:shadow-outline-green focus:border-green-700 sm:text-sm sm:leading-5"
                            >
                              Confirm
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </Dialog>
                </Transition>
              </div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default CompanyDetail;
