import React, { useState } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import axios from "axios";
import { Dialog, Transition } from "@headlessui/react";
import { Fragment } from "react";

function Tableau({ tab }) {
  const navigate = useNavigate();
  const goToDashboard = () => {
    navigate("/dashboard");
  };

  const handleEdit = (id) => {
    navigate(`/edit-course/${id}`);
  };

  const refreshPage = () => {
    window.location.reload();
  };

  const [isOpen, setIsOpen] = useState(false);
  const [courseId, setCourseId] = useState(null);

  const handleCloseDialog = () => {
    setIsOpen(false);
  };

  const handleOpenDialog = (id) => {
    setCourseId(id);
    setIsOpen(true);
  };

  const handleDelete = () => {
    axios
      .delete(`http://localhost:3000/training-courses/${courseId}`)
      .then((response) => {
        console.log("deleted");
        refreshPage();
      })
      .catch((error) => {
        console.error(error);
      });
    setIsOpen(false);
  };
  const [isClicked,setIsClicked]= useState(false)
  const handleClick = ()=>{
    setIsClicked(true)
  }
  return (
    <div className="container mx-auto my-8">
      <h2 className="text-2xl font-bold mb-4">Training Course Table</h2>
      <button onClick={handleClick} className="mb-2 bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded-md focus:outline-none focus:shadow-outline">
        <Link to="formation">Add Course</Link>
      </button>
      <Outlet/>
      ''
    </div>
  );
}

export default Tableau;
