import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import LanguageDetector from "i18next-browser-languagedetector";


const resources =  {
    en: {
        translation: {
            "Search":"Search",
            "title":"title",
            "price":"price",
            "APK FILE":"APK FILE",
            "Edit":"Edit",
            "Delete":"Delete",
            "Previous":"Previous",
            "Next":"Next",
            "Accounts":"Accounts"
        }
    },
    fr: {
        translation: {
            "Search":"Recherche",
            "title":"titre",
            "price":"prix",
            "APK FILE":"FICHIER APK",
            "Edit":"Editer",
            "Delete":"Supprimer",
            "Previous":"Précédent",
            "Next":"Suivant",
            "Accounts":"Accounts"
        }
    }
};

i18n
  .use(LanguageDetector)
  .use(initReactI18next) // passes i18n down to react-i18next
  .init({
    resources,
    lng: "en", 
    interpolation: {
      escapeValue: false 
    },
    react:{
        useSuspense:false
    }
});

export default i18n;
